import moment from 'moment';
export default {
  metaInfo: {
    title: "News"
  },
  data: function data() {
    return {
      tab: 0,
      news: []
    };
  },
  mounted: function mounted() {
    this.news = this.$page.news.edges;
  },
  watch: {
    tab: function tab(val) {
      if (this.tab === 0) {
        this.showAllNews();
      } else {
        this.showNewsByType(val);
      }
    }
  },
  methods: {
    showAllNews: function showAllNews() {
      this.news = this.$page.news.edges;
    },
    showNewsByType: function showNewsByType(val) {
      this.news = this.$page.news.edges.filter(function (edge) {
        return edge.node.category.id === val;
      });
    },
    formatDate: function formatDate(date) {
      return moment(date).format('MMMM Do YYYY , h:mm a');
    }
  }
};
