import moment from 'moment'




export default {
  metaInfo: {
    title:  "News"
  },
  data() {
    return {
      tab:0,
      news: []

    }
  },
  mounted(){
    this.news = this.$page.news.edges
  },
  watch:{
    tab(val){
      if(this.tab === 0){
        this.showAllNews()
      }else{
        this.showNewsByType( val )
      }
    }
  },
  methods:{
    showAllNews(){
      this.news = this.$page.news.edges
    },
    showNewsByType(val){
      this.news = this.$page.news.edges.filter((edge) =>{
        return edge.node.category.id === val
      })
    },
    formatDate(date){
      return moment(date).format('MMMM Do YYYY , h:mm a')
    }
  }
}
