result:
  body.navbar_body{
  position: relative;
  z-index: 9999;
  margin: 0;
  background-color: #f1f1f1;
  font-family: Arial, Helvetica, sans-serif;

}
#navbar{
  z-index: 999999999;
  background-color: rgba(37, 6, 54, 0.98);
  position: fixed;
  top: 0px;
  width: 100%;
  display: block;
  transition: top 0.3s;
  padding-top: 10px;
}
a.navbar_a{
  z-index: 99999;
  float: right;
  display: block;
  color: #f2f2f2;
  text-align: center;
  padding: 15px;
  text-decoration: none;
  font-size: 17px;
  margin-right: 100px;
}
a.navbar_a:hover{
  z-index: 99999;
  background-color: #ddd;
  color: black;
}
h2.navbar_h2{
  z-index: 99999;
  float: left;
  display: block;
  color: #f2f2f2;
  text-align: center;
  padding: 15px;
  padding-bottom: 0;
  text-decoration: none;
  font-size: 17px;
}
.search{
  position: absolute;
  top:12%;
  left:25%;

}
v-row{
  position: relative;
}

v-card-actions{
  position: absolute;
  bottom: 10000px;
}
v-card{
  margin:1000px !important;

}
